pragma solidity >=0.6.0 <0.8.0;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC20/SafeERC20.sol";
import "truffle/console.sol";

contract CrowdSale {
    using SafeMath for uint256;
    // using SafeERC20 for IERC20;

    IERC20 private _token;
    uint256 private _rate;
    uint256 private _weiRaised;
    address payable private _wallet;

    event TokensPurchased(
        address indexed purchaser,
        address indexed beneficiary,
        uint256 value,
        uint256 amount
    );

    constructor(uint256 rate, address payable wallet, IERC20 token) public {
        _rate = rate;
        _wallet = wallet;
        _token = token;
    }

    function countTokens(uint256 amount) private view returns (uint256) {
        require(amount > 0 && _rate != 0, "count error");
        return amount.mul(_rate);
    }

    function purchaseToken(address beneficiary, address purchaser,  uint256 weiAmount) public payable {
        // uint256 weiAmount = msg.value;

        require(weiAmount > 0, "value error");
        uint256 tokenAmount = countTokens(weiAmount);
        _weiRaised = _weiRaised.add(weiAmount);
        _processPurchase(beneficiary, tokenAmount, weiAmount);

        emit TokensPurchased(purchaser, beneficiary, weiAmount, weiAmount);
    }

    function _processPurchase(address beneficiary, uint256 tokenAmount, uint256 weiAmount) private {
        _token.transfer(beneficiary, tokenAmount);
    }

